function getrandom(x) { var h = x.feed.entry[0]; for (var j = 0; j < h.link.length; ++j) { if (h.link[j].rel == 'alternate') { var z = h.link[j].href; } } document.getElementById('rbutton').href = z; } function random(s) { var total = parseInt(s.feed.openSearch$totalResults.$t, 10), randomchoice = Math.floor(Math.random() * total), c = document.createElement('script'); c.src = '/feeds/posts/summary?max-results=1&start-index=' + randomchoice + '&alt=json-in-script&callback=getrandom'; c.type = 'text/javascript'; document.getElementsByTagName('head')[0].appendChild(c); } (function () { var e = document.createElement('script'); e.src = '/feeds/posts/summary?max-results=0&alt=json-in-script&callback=random'; e.type = 'text/javascript'; document.getElementsByTagName('head')[0].appendChild(e); })(); $(document).ready(function(){ var q = $('li.tl-menuRecent'); q.each(function () { var e = $(this); var indexnumber=Math.random(); var w = 4 - $('ul', e).length; var domain = $('.tl-bloglink').attr('data-url'); var f = e.attr('data-label'), g = domain + '/feeds/posts/summary/-/' + f + '?max-results=' + w + '&alt=json-in-script'; if (w<4) { e.find('ul').wrap('<div class="nav-post-list"></div>'); } else if (w == 4) { e.append('<div class="nav-post-list"></div>'); } $('.nav-post-list', e).append('<div class="nav-item"></div>'); $.ajax({ type: 'GET', url: g, async: true, contentType: "application/json", dataType: 'jsonp', success: function (json) { for (var i = 0; i < json.feed.entry.length; i++) { var h = json.feed.entry[i]; for (var j = 0; j < h.link.length; j++) { if (h.link[j].rel == 'alternate') { var l = h.link[j].href; break; } } try { var k = h.media$thumbnail.url.replace("s72-c", "w350-h250-c"),b=h.media$thumbnail.url.replace("s72-c", "s1600-no") } catch (m) { var k = 'http://3.bp.blogspot.com/-zvVhSlsk0xg/VfzbhOQ1zPI/AAAAAAAAAt0/hRsGBhuP2a0/s1600/No%2BImage%2BAvailable.png'; } var z = h.title.$t; var nnitem = "<div class='n-item'><div class='tl-menuMag'><div class='tl-mag