//Image Animation
var mag_news= (function($) {
	"use strict";
	var hasTouch = false;
	return {
			init: function() 
		{			
			$('.content-wrapper img').addClass('no-display');

			$('.content-wrapper img').one('inview', function() {
				$(this).addClass('appear');
			});
		},
	}; 

})(jQuery);

jQuery(function($) {
	mag_news.init();
});
