  e.preventDefault();
  if(search.is('.active') && $(e.target).is(search)){
    search.removeClass('active');
  }
  else{
    search.addClass('active');
    search.find('input').focus();
  }
});
$('body').click(function(e){
  if(	search.is('.active') &&
     !$(e.target).is('.search, .search form, .search input')){
    search.removeClass('active');
  }
});
