function stripHtmlTags(s,max){return s.replace(/<.*?>/ig, '').split(/\s+/).slice(0,max-1).join(' ')}

function createSummaryAndThumb(pID,title,url,date,comment,tag,author){
 var posturl= url;
 var title=title;
 var date = date;
 var comment = comment;
 var tag = tag;
